            var appid = '190191627665278';

            window.fbAsyncInit = function () {
                FB.init({
                    appId: appid,
                    status: true,
                    cookie: true,
                    xfbml: true,
                    oauth: true
                });

                // fetch the status on load
                FB.getLoginStatus(handleSessionResponse);

                $('#fconnectlogin').bind('click', function () {
                    FB.login(login, { scope: 'email, publish_stream, offline_access' });
                });

                $('#fconnectlogout').live('click', function () {
                    FB.logout(handleSessionResponse);
                });

                $('#disconnect').bind('click', function () {
                    FB.api({ method: 'Auth.revokeAuthorization' }, function (response) {
                        clearDisplay();
                    });
                });

            };
                                
            (function () {
                var e = document.createElement('script'); 
                e.async = true;
                e.src = document.location.protocol + '//web.archive.org/web/20120504005345/http://connect.facebook.net/en_US/all.js';
                document.getElementById('fb-root').appendChild(e);
            } ());

            function getFacebookUser(callback, desiredData)
            {
                var desiredDataString = desiredData.join(',');

                FB.api(
                    {
                        method: 'fql.query',
                        query: 'SELECT '+ desiredDataString + ' FROM user WHERE uid= me()'
                    },
                    function (response) 
                    {
                        var user = response[0];
                        callback(user);
                    }
                );
            }   

            function login(response) {
                // if we dont have a session, just hide the user info
                if (!response.authResponse) {
                    clearDisplay();
                    return;
                }

                    //We have the session.
                
                        //Check for existing SNU.
                        var onSuccess = function (result, context) 
                        {
                            if(result=== "")
                            {
                                getFacebookUser(function(user) {FBConnectNewCharacterModal.open(user.name, user.email); }, ['name', 'email']);
                            }
                            else{
                                //Drop cookie
                                document.getElementById("SNAccessToken").value = FB.getAccessToken();
                                document.getElementById("FacebookAssociation").value = "true";
                                if(document.forms.length == 1){
                                    $('#FacebookLoginForm')[0].submit();
                                }else{
                                    document.forms[1].submit();
                                }
                            }
                        };
                        var onError = function (result, context) { };
                        UserCheck.GetSocialNetworkUserName(1, FB.getAccessToken(), onSuccess, onError);
                    
            } 

            // no user, clear display
            function clearDisplay() {
                $('#user-info').fadeOut(1000);
                $('#facepile').fadeOut(1000);
            }

            // handle a session response from any of the auth related calls
            function handleSessionResponse(response) {
                // if we dont have a session, just hide the user info
                if (!response.authResponse) {
                    clearDisplay();
                    return;
                }

                //Don't show the details if user is not logged into Roblox.
                  
                      return;
                  

                // if we have a session, query for the user's profile picture and name
                getFacebookUser(
                    function (user) 
                    {
                        var markup = '<table>' +
                                        '<tr>' +
                                            '<td>' +
                                            '<img style="width:75px; margin-top:5px;" src="' + user.pic + '">' +
                                            '</td>' +
                                            '<td>' +
                                            '<div style="font-size:10px;">Hi ' + user.name + '<br/>Your Facebook is connected.<br/></div>' +
                                            '</td>' +
                                        '</tr>' +
                                    '</table>';

                         $('#user-info').html(markup).fadeIn();  
                    
                    }, 
                    ['name', 'pic']
                );
            }

        </script>

        

        

